import React, {Component} from "react";
import "./App.css";
export default class App extends Component{

  state={
    todoData : [
      {
        id:"1",
        title : "공부하기",
        completed:true
      },
      {
        id:"2",
        title : "청소하기",
        completed:true
      },
      {
        id:"3",
        title : "섹스하기",
        completed:true
      },
    ],
    value:""
  } 
  getStyle = () =>{
    return {
      padding: "10px",
      borderBottom : "1px #ccc dotted",
      textDecorataion: 'none'
    }
  }

  render(){
    return(
      <div className="container">
        <div className="todoBlock">
          <div className="title">
            <h1>할일목록</h1>
            {this.state.todoData.map((data)=>(
              <div stlye={this.getStyle()} key={data.id}>{data.title}</div>
            ))}
          </div>
        </div>
      </div>
    )
  }

}
